import React, {useEffect, useState} from 'react'
import img from './sewing.jpg' // relative path to image 
//import Form from "./components/Form";
//import FormStatus from "./components/FormStatus";

import { Outlet, Link, history } from "react-router-dom";
function App() {

  const [backendData, setBackendData] = useState([{nom: 'Chargement...', description: 'Chargement...'}])
  //const [itemId, setItemId] = useState(0)
  //?hide and show the description bellow the photo
  //?const [isVisible, setIsVisible] = useState(false);
  //?<p className={isVisible ? 'card-text' : 'card-text text-truncate'} onClick={handleClick}>{article.description}</p>
  //?const handleClick = () => isVisible === true ? setIsVisible(false) : setIsVisible(true);


  const openItemPage = (id) => {
    //setItemId(id)
    //<Link to={`/Item/${id}`}>Product 1</Link>
    history.push(`/Item/${id}`);
    console.log(id);
  }
  useEffect(() => {
    fetch('/senddatatofront').then(response => response.json()).then(data => setBackendData(data))
  }, [])

  console.log(backendData);
  console.log("HOME PAGE")
  return (
    <div className="d-flex flex-wrap justify-content-center">
      <nav>
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/Item/1">Blogs</Link>
          </li>
        </ul>
      </nav>
      {(typeof backendData === 'object') ? backendData.map((article, index) => {
        return (
          <div id={index} key={index} className="p-2 article w-25 d-flex flex-column" onClick={openItemPage(index)}>
            <h2 className='text-center'>{article.nom}</h2>
            <div className="card w-100">
              <img className="card-img-top" src={img} alt="img"/>
              <div className="card-body">
                <p className=''>{article.description}</p>
              </div>
            </div>
          </div>
        )
      }) : <p>{backendData}</p> }
      <Outlet />
    </div>
  )
}

export default App
