import img from '../sewing.jpg' // relative path to image 
import { useParams } from "react-router-dom";
import { Outlet, Link } from "react-router-dom";
import React, {useEffect, useState} from 'react'

function Item() {
  const { id } = useParams();
  const [backendData, setBackendData] = useState([{nom: 'Chargement...', description: 'Chargement...'}])

  useEffect(() => {
    fetch('/senddatatofront').then(response => response.json()).then(data => setBackendData(data))
  }, [])
  setBackendData(backendData[id]);
  console.log(backendData);
  console.log(id);
  return (
    <div className="d-flex flex-wrap justify-content-center">
      <nav>
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/Item/1">Blogs</Link>
          </li>
        </ul>
      </nav>
      {(typeof backendData === 'object') ? backendData.map((article, index) => {
        return (
          <div id={index} key={index} className="p-2 article w-25 d-flex flex-column">
            <h2 className='text-center'>{article.nom}</h2>
            <div className="card w-100">
              <img className="card-img-top" src={img} alt="img"/>
              <div className="card-body">
                <p className=''>{article.description}</p>
              </div>
            </div>
          </div>
        )
      }) : <p>{backendData}</p> }
      <Outlet />
    </div>
  )
}

export default Item;
