        <h2 className='text-center'>{backendData.nom}</h2>
        <div className="card w-100">
          <img className="card-img-top" src={img} alt="img"/>
          <div className="card-body">
            <p className=''>{backendData.description}</p>
          </div>
        </div>
      </div>
